void.string:
forall if (ismembrane("na_ion")) {

	print "Rev pot of na being changed for section ", secname()
	ena = 45
}


forall if (ismembrane("k_ion")) {

	print "Rev pot of k being changed for section ", secname()
	ek = -85
}



forall if (ismembrane("ca_ion")) {

	print "Rev pot of k being changed for section ", secname()

	// Should be: cai = 4e-5 

	cai = 4e-5

	cao = 2.4

                             eca = 12.5*log(cao/cai)
                              ion_style("ca_ion", 1, 1, 0, 0, 0)
}

str someTempCompName

float restCa = 4e-5
float initCa = 4e-5



if ({exists  /cells/SampleCellGroup})

    foreach someTempCompName ({el /cells/SampleCellGroup/#/#})

         if ({exists  {someTempCompName}/cad})

            echo "Resetting Ca resting conc of: " {someTempCompName}/cad

            if (units == "GENESIS Physiological Units")
                setfield {someTempCompName}/cad Ca {initCa / 1000000}
                setfield {someTempCompName}/cad C   {{initCa - restCa} / 1000000}
            else
                setfield {someTempCompName}/cad Ca initCa
                setfield {someTempCompName}/cad C {initCa - restCa}
            end

        end

    end

end
